css:
 /*pc*/@media (min-width: 601px){
.top-bar {
    background: #e5e5e5;
    width: 100%;
   }
.stamps {
  max-width: 30%;}
.py-xl-5 {
    margin-top: -24px;
}
   .my-lg-5 {
    margin-bottom: -3rem;
}

   #llCaptureForm{ border-radius:10px; border:1px solid #310000;}
   .button-bar{ padding-top:15px; padding-bottom:15px; border:none;}
   .news{ width:400px;}
   h1 {font-size: 25px;color:#310000;}
h2{font-size:22px;}
   h3{font-size:21px;}
   h4{font-size:20px;}
   h5{font-size:19px;}
   .products-carousel__title {color: #1c0900;font-size:21px;}
   .glide__arrows--outer .glide__arrow--left {
    left: 5px;
}.glide__arrows--outer .glide__arrow--right {
    right: 5px;}
   body {font-family: Outfit;font-size:18px;}
   .product-card__offer-stamp {
     background-color: #125a48;border-radius: 20px;}
   .glide__bullets {
    bottom: 1rem;
}
   .header__nav {
    font-size: 16px;
    margin-top: -40px;
    text-align: center;
    margin-left: 160px;


}
   .mt-lg-0 {
    margin-top: -40px;
}
  .sobre_cell_title col-12 {
    flex: 0 0 100%;
    max-width: 100%;
}
   .celltronics-mobileonly{display:none;}

   .col-md-6 {max-width:50%;}
}


/*mobile*/@media (max-width: 600px) {
    .top-bar {display:none;}
  body {font-family: Outfit;font-size:16px;}
h1 {font-size: 20px;color:#310000;}
h2{font-size:18px;}
h3{font-size:17px;}
   h4{font-size:16px;}
   h5{font-size:16px;}
  .html-clearfix{ font-family: Outfit;font-size:19px;color:#242323;}
.products-carousel__title {color: #1c0900;font-size:16px;}
  .btn {
    font-size: 23px;
}.py-xl-5 {
    margin-top: -44px;
}
  .menu__item{font-size:18px;}
  .footer__links {font-size:23px;}

  .col-md-7 {
    flex: 0 0 58.33333333%;
    max-width: 58.33333333%;
}.sobre_cell_container {
    text-align: center;
}
.celltronics-mobileonly {
    display: flex!important;
    margin-top: 7%;
}
  .celltronics-deskonly{display:none;}
  .col-12 {
    flex: 0 0 100%;
    max-width: 100%;
}
}
/*responsivo*/
.html-clearfix{ font-family: Outfit;font-size:18px;color:#242323;}
.header {background: #ffffff;box-shadow: 5px 5px 5px #38241452;
  border-bottom: 1px solid #ffffff;}
.i-search{color: #310000;}
#search-input{color: #ededed;}
.header__toggler{color:#310000;}
.top-bar__page-links a {font-weight: 300;color:#310000;}
.i-user{color:#310000;}
.i-facebook{color:#310000;}
.i-youtube{color:#310000;}
.i-instagram{color:#310000;}
.i-pinterest{color:#310000;}
.i-times{color:#e5ddc6;}
.menu {background: #0b1e2c;}
.menu__list {background-color: #0b1e2c;}
.menu__item {border-bottom: var(--border-width) solid #684e3c;}
.menu__item {color: #e5ddc6; }
.menu__item:hover {background: #000;}
.menu__footer a {color: #fff;text-decoration: none;}
.glide__arrow{color: #310000;}
.page-title__head{color: #310000; font-size:25px;}
.btn-primary {
    background-color: #043629;
    border-color: #043629;border-radius: 20px
}


.i-shopping-bag{color:#51746b;}
.info-bar{background: #51746b;color: #e3d4c1;margin-left: 0;margin-right: 0;
    max-width: 100%;width: 100%;}
.footer{background: #e5e5e5;border-top: var(--spacer-1) solid #51746b;}
.footer__store{color: #310000;}
p{color: #310000;}
.footer__links i{color:#e5e5e5;}
.footer__title{color: #310000;font-weight: 500;}
.footer__credits {color:#310000;border-top: var(--border-width) solid #dfdfdf;}
.ecom-credits{background: #e5e5e5;color: #310000}
.go-to-top{color:#310000;}
.brands-carousel {margin-top:15px;margin-bottom:15px;}
.header__logo {max-height: 120px;}
.header__search-input {border: 1px dotted #e5d7bb;font-size: 16px;border-radius: 20px;}
.header__nav{text-transform: uppercase;}
.header__nav a{background: transparent;}
.header__nav a:hover{background: transparent;text-decoration: none;
  font-weight: 600;border-bottom: 2px solid #51746b;}
.header__toggler-icon {color: #310000;}


.rounded-icon {border: 1px solid #07382b;}
.i-shipping-fast{color:#07382b;}
.info-bar__text{color:#fff8f0; font-size:14px;}
.i-credit-card{color:#07382b;}
.btn-outline-secondary {border-color: #310000;color: #310000;}

.info-bar__title{color:#fff8f0; font-size:18px;}
.checkout__step--current {
  color: #0f4a3c;}
a:hover {color: #fff;text-decoration: none;}
.i-phone{color:#310000;}

.prices--big .prices__value {font-size: 33px;}
.product__short-stock{display:none;}
.share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
.lead {font-size: 16px; color:#242323;}
.top-bar__contacts-container{color:#310000;}
.hero-banner {  margin: 0px;}
.prices__value {color:#000000;}
.btn-outline-secondary {border-color: #310000;color: #310000;}
#storefront-app>div>h1 {color: #310000;}
.app__header {background: #ffffff;}
.login__info-title{color:#310000;}
.list-group-item.active {background-color: #e7e0bd;border-color: #e7e0bd;
  color: #310000;}
.btn-success:hover {background-color:#00262a;border-color:#00262a; 
  color: var(--white);}
.btn-success {
    background-color: #0b6850;
    border-color: #0b6850;
    color: var(--white);
}
.btn-outline-secondary {
    border-color: #024429;
    color: #024429;
}
.btn-outline-secondary a:hover{
    background: #024429;
    color:#fff;
}
.summary__amount__row--total strong {
    color: #043629;
}
.discount-applier__intro {
    color: #043629;
}
.products-carousel__title a{background: transparent;}
.products-carousel__title:before {background:transparent;}
.products-carousel__title {display: block;padding-bottom: 20PX;
  padding-top: 50px;}
.products-carousel__title:before{margin-top: 50px;margin-bottom: 50px;}
.product-card {background-color: #fff;margin-bottom:30px !important;
  border-radius: 10px;box-shadow: 1px 1px 12px #b7b7b742;}
.my-lg-5 {margin-top: 0rem;padding-bottom: 1px;}
.banner {margin-top: 0;}
.products-carousel {background: #fff;}
.banner-slider{margin-bottom: -4px;background-attachment: fixed;
  background-position: center;background-repeat: no-repeat;
  background-size: cover;}
.products-carousel {margin-top: 0px;}
.page--home .collection-shelf {margin-bottom: 0px !important}
.page--home .my-lg-5{background:#fff;}
.__pages_nossas-linhas .collection-shelf {margin-bottom: 0px !important}
.__pages_nossas-linhas .my-lg-5{background: #fff;}
.__pages_nossas-linhas{background:#6c3d1e;}
.banner {
    display: block;
      box-shadow: 0px 0px 0px #2e2e2e5e;
}
.app__logo {max-height: 120px;
    max-width: 300px;}
#storefront-app {color: #201c17;}
.app__contact  {color: #043629;}
.login__info-title {color: #2a2620;}
.login__info {border-bottom: 5px solid #043629;}
.app__footer {
  background: linear-gradient(180deg,var(--light) 0,var(--body-bg) 90%);
    border-top: var(--spacer-1) solid #ededed;}
.stamps img {
    height: auto;
    max-height: 70px;
    max-width: 70%;
}
.app__contact {color: #043629;}
#collapse-Brands{display:none;}
.prices__installments {
    font-weight: 700; font-size:16px; color:#686868;
}
.prices__discount{
    font-weight: 700; font-size:16px; color:#444646;
}
.prices__value {
    display: block;
    font-size: 21px;color:#053136;
}
.summary__amount__row--total  {
    color: #03240d;
}
.order-info__financial-status--pending {
    color: #ff8507;
}
.order-info__timeline-status--pending {
    border-color: #ff8507;
}
.order-info__new {
  color: #080c0b;}
.account__greetings i{color: #052814;}
.product-card__favorite .active {
    color: #910c19;
}
.badge-info {
    background-color: #066245;
    color: var(--white);
}
.badge-success {
    background-color: #066c2f;
    color: var(--white);
}
.search-engine__filter--Brands{display:none;}
.prices:not(.prices--big) .prices__installments {
    font-weight: 700;
}
.__faria-coffee{background-color:#e1e1e1;}
.__a-coffee{background-color:#e1e1e1;}
.__filippeto {background-color:#e1e1e1;}
.col-md-4{margin-bottom: 25px;}
.product-card__name{font-size: 18px;}
.btn{font-size:18px;}
.btn-primary:not(:disabled):not(.disabled):active{
  background-color: #51746b;
    border-color: #51746b;
    color: var(--primary-dark-yiq);
}
.btn-primary:hover {
      background-color: #51746b;
  border-color: #51746b;}
.btn-outline-secondary {
  border-radius: 20px;}
#ws_icon_chat img {
    width: 80px !important;
}
#ws_icon_chat {
    position: fixed;
    bottom: 10px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;
}
.product__unavailable {
    color: #ff5707;
    font-size: var(--font-size-lg);
    font-weight: var(--font-bold);
    margin-bottom: var(--spacer-3);
}

.sobre_cell_title h4 {
    color: #043629;
    font: normal normal 400 30px/40px Outfit;
    letter-spacing: 0;
    margin-bottom: 20px;
}
.sobre_cell_text h5 {
    color: #333;
    font: normal normal 700 20px/40px Outfit;
    letter-spacing: 0;
    margin: 20px 0;
}
.sobre_cell_text a, .sobre_cell_video .celltronics-mobileonly {
   background: #043629 0 0 no-repeat padding-box;
   border-radius: 50px;
   color: #fff;
   font: normal normal 700 18px/40px Outfit;
   letter-spacing: 0;
   padding: 5px 60px;
   text-transform: uppercase;
}
.sobre_cell_text a {
   width: 60%;
}

}
